mapper.update:
UPDATE t_activity
name = #{name}, cover_url = #{coverUrl}, start_time = #{startTime}, end_time = #{endTime}, category = #{category}, venue_id = #{venueId}, organizer = #{organizer}, contact_phone = #{contactPhone}, intro = #{intro}, address_cache = #{addressCache}, apply_user_id = #{applyUserId}, apply_status = #{applyStatus}, reject_reason = #{rejectReason}, online_status = #{onlineStatus}, view_count = #{viewCount}, favorite_count = #{favoriteCount}, is_deleted = #{isDeleted}, update_time = #{updateTime}
WHERE id = #{id} AND is_deleted = 0

mapper.select:
SELECT a.id, a.name, a.cover_url AS coverUrl, a.start_time AS startTime, a.end_time AS endTime, a.category, a.venue_id AS venueId, v.name AS venueName, a.address_cache AS addressCache, a.online_status AS onlineStatus FROM t_activity a LEFT JOIN t_venue v ON a.venue_id = v.id
a.is_deleted = 0 AND a.apply_status = #{applyStatus} AND a.online_status = #{onlineStatus}
AND a.name LIKE CONCAT('%', #{query.name}, '%')
AND a.venue_id = #{query.venueId}
AND a.start_time >= #{query.startTimeFrom}
AND a.start_time <= #{query.startTimeTo}
ORDER BY a.start_time ASC

SELECT
FROM t_activity WHERE is_deleted = 0 AND id IN
#{id}
