mapper.insert:
INSERT INTO t_admin (
    username, password, real_name, phone, email,
    role_code, is_super, status, remark
) VALUES (
    #{username}, #{password}, #{realName}, #{phone}, #{email},
    #{roleCode}, #{isSuper}, #{status}, #{remark}
)

mapper.update:
UPDATE t_admin
real_name = #{realName}, phone = #{phone}, email = #{email}, remark = #{remark}, role_code = #{roleCode}, is_super = #{isSuper}, status = #{status}, password = #{password}, update_time = NOW()
WHERE id = #{id}

UPDATE t_admin
SET status = #{status}, update_time = NOW()
WHERE id = #{id}

UPDATE t_admin
SET is_deleted = 1, update_time = NOW()
WHERE id = #{id}
