mapper.select:
SELECT r.id, r.reporter_id, reporter.username AS reporter_name, reporter.nickname AS reporter_nickname, r.target_user_id, target_user.username AS target_user_name, target_user.nickname AS target_user_nickname, r.target_type, r.target_id, r.reason_type, r.reason, r.screenshot_urls, r.status, r.violation_flag, r.result_remark, r.create_time, r.update_time FROM t_content_report r LEFT JOIN t_user reporter ON r.reporter_id = reporter.id LEFT JOIN t_user target_user ON r.target_user_id = target_user.id WHERE r.is_deleted = 0
AND r.status = #{status}
AND r.target_type = #{targetType}
AND ( r.reason LIKE CONCAT('%', #{keyword}, '%') OR reporter.username LIKE CONCAT('%', #{keyword}, '%') OR reporter.nickname LIKE CONCAT('%', #{keyword}, '%') OR target_user.username LIKE CONCAT('%', #{keyword}, '%') OR target_user.nickname LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY r.create_time DESC
