mapper.select:
SELECT a.id, a.user_id, u.nickname AS user_nickname, u.avatar AS user_avatar, a.title, a.content, a.cover_url, a.status, a.like_count, a.comment_count, a.create_time FROM t_exchange_article a LEFT JOIN t_user u ON a.user_id = u.id WHERE a.is_deleted = 0
AND a.status = #{status}
AND a.user_id = #{userId}
AND ( a.title LIKE CONCAT('%', #{keyword}, '%') OR a.content LIKE CONCAT('%', #{keyword}, '%') OR u.nickname LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY a.create_time DESC

mapper.update:
UPDATE t_exchange_article
SET status = #{status},
    audit_remark = #{auditRemark},
    update_time = #{updateTime}
WHERE id = #{id}
  AND is_deleted = 0

UPDATE t_exchange_article
SET like_count = IFNULL(like_count, 0) + #{delta},
    update_time = #{updateTime}
WHERE id = #{id}
  AND is_deleted = 0

UPDATE t_exchange_article
SET comment_count = IFNULL(comment_count, 0) + #{delta},
    update_time = #{updateTime}
WHERE id = #{id}
  AND is_deleted = 0
