mapper.select:
SELECT c.id, c.article_id, c.user_id, c.content, c.parent_id, c.mention_user_id, c.like_count, c.create_time FROM t_exchange_comment c WHERE c.parent_id IN
#{id}
AND c.is_deleted = 0 ORDER BY c.create_time ASC

mapper.update:
UPDATE t_exchange_comment
SET is_deleted = 1,
    update_time = #{updateTime}
WHERE id = #{id}
  AND is_deleted = 0

UPDATE t_exchange_comment
SET like_count = IFNULL(like_count, 0) + #{delta},
    update_time = #{updateTime}
WHERE id = #{id}
  AND is_deleted = 0
