mapper.select:

WHERE is_deleted = 0 AND status = 1
AND id IN (
    SELECT DISTINCT menu_id FROM t_role_menu WHERE role_code IN

        #{role}

)
ORDER BY order_num DESC, id ASC

SELECT id, menu_id, title, auth_mark, sort, remark, create_time, update_time FROM t_menu_permission WHERE menu_id IN
#{menuId}
ORDER BY sort DESC, id ASC

SELECT DISTINCT p.id, p.menu_id, p.title, p.auth_mark, p.sort, p.remark, p.create_time, p.update_time FROM t_menu_permission p INNER JOIN t_role_menu_permission rp ON p.id = rp.permission_id WHERE rp.role_code IN
#{role}
ORDER BY p.sort DESC, p.id ASC
