mapper.update:
UPDATE t_message_feedback
title = #{title}, content = #{content}, contact_phone = #{contactPhone}, contact_email = #{contactEmail}, status = #{status}, update_time = #{updateTime}
WHERE id = #{id} AND is_deleted = 0

UPDATE t_message_feedback
SET is_deleted = 1,
    update_time = #{updateTime}
WHERE id = #{id}
  AND is_deleted = 0

mapper.select:
SELECT f.id, IFNULL(f.title, f.content) AS title, f.update_time AS deleted_time, u.username AS operator, CONCAT('反馈状态：', CASE f.status WHEN 1 THEN '已反馈' ELSE '待反馈' END) AS extra_info FROM t_message_feedback f LEFT JOIN t_user u ON f.user_id = u.id WHERE f.is_deleted = 1
AND (f.title LIKE CONCAT('%', #{keyword}, '%') OR f.content LIKE CONCAT('%', #{keyword}, '%') OR u.nickname LIKE CONCAT('%', #{keyword}, '%'))
AND f.update_time >= #{startTime}
AND f.update_time <= #{endTime}
ORDER BY f.update_time DESC

mapper.update:
UPDATE t_message_feedback
SET is_deleted = 0,
    update_time = #{updateTime}
WHERE id = #{id}
  AND is_deleted = 1

mapper.delete:
DELETE FROM t_message_feedback
WHERE id = #{id}
  AND is_deleted = 1
