mapper.select:
SELECT f.id, f.user_id, f.type, f.title, f.content, f.status, f.create_time, ( SELECT COUNT(1) FROM t_message_feedback_comment c WHERE c.feedback_id = f.id AND c.is_deleted = 0 ) AS comment_count FROM t_message_feedback f WHERE f.is_deleted = 0
AND f.type = #{type}
ORDER BY f.create_time DESC

SELECT f.id, f.user_id, u.username AS user_name, u.nickname AS user_nickname, f.type, f.title, f.content, f.contact_phone, f.contact_email, f.status, f.create_time, f.update_time FROM t_message_feedback f LEFT JOIN t_user u ON f.user_id = u.id WHERE f.is_deleted = 0
AND f.type = #{type}
AND f.status = #{status}
AND ( f.title LIKE CONCAT('%', #{keyword}, '%') OR f.content LIKE CONCAT('%', #{keyword}, '%') OR u.username LIKE CONCAT('%', #{keyword}, '%') OR u.nickname LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY f.create_time DESC

mapper.update:
UPDATE t_message_feedback
type = #{type}, title = #{title}, content = #{content}, contact_phone = #{contactPhone}, contact_email = #{contactEmail}, status = #{status}, update_time = #{updateTime}
WHERE id = #{id} AND is_deleted = 0

UPDATE t_message_feedback
SET is_deleted = 1,
    update_time = #{updateTime}
WHERE id = #{id}
  AND is_deleted = 0
