mapper.update:
UPDATE t_notice
title = #{title}, content = #{content}, publish_time = #{publishTime}, author = #{author}, status = #{status}, view_count = #{viewCount}, update_time = #{updateTime}
WHERE id = #{id} AND is_deleted = 0

UPDATE t_notice
SET view_count = view_count + 1,
    update_time = #{updateTime}
WHERE id = #{id}
  AND is_deleted = 0
  AND status = 1

mapper.select:
SELECT id, title, update_time AS deletedTime, NULL AS operator, CONCAT('发布人：', COALESCE(author, '未知')) AS extraInfo FROM t_notice WHERE is_deleted = 1
AND title LIKE CONCAT('%', #{keyword}, '%')
AND update_time >= #{startTime}
AND update_time <= #{endTime}
ORDER BY update_time DESC

mapper.update:
UPDATE t_notice
SET is_deleted = 0,
    update_time = #{updateTime}
WHERE id = #{id}
  AND is_deleted = 1
