mapper.update:
UPDATE t_scenic_spot
name = #{name}, image_url = #{imageUrl}, level = #{level}, ticket_price = #{ticketPrice}, address = #{address}, open_time = #{openTime}, intro = #{intro}, phone = #{phone}, website = #{website}, longitude = #{longitude}, latitude = #{latitude}, sort = #{sort}, update_time = #{updateTime}
WHERE id = #{id} AND is_deleted = 0

mapper.select:
SELECT
FROM t_scenic_spot WHERE is_deleted = 0 AND id IN
#{id}

SELECT id, name AS title, update_time AS deletedTime, NULL AS operator, CONCAT('地址：', COALESCE(address, '未维护')) AS extraInfo FROM t_scenic_spot WHERE is_deleted = 1
AND name LIKE CONCAT('%', #{keyword}, '%')
AND update_time >= #{startTime}
AND update_time <= #{endTime}
ORDER BY update_time DESC

mapper.update:
UPDATE t_scenic_spot
SET is_deleted = 0,
    update_time = #{updateTime}
WHERE id = #{id}
  AND is_deleted = 1
