mapper.select:
SELECT

FROM t_user_favorite
WHERE user_id = #{userId}
  AND target_type = #{targetType}
  AND target_id = #{targetId}
LIMIT 1

mapper.update:
UPDATE t_user_favorite
SET is_deleted = 0,
    update_time = #{updateTime}
WHERE id = #{id}
  AND is_deleted = 1

UPDATE t_user_favorite
SET is_deleted = 1,
    update_time = #{updateTime}
WHERE user_id = #{userId}
  AND target_type = #{targetType}
  AND target_id = #{targetId}
  AND is_deleted = 0

mapper.select:
SELECT

FROM t_user_favorite
WHERE user_id = #{userId}
  AND is_deleted = 0

      AND target_type = #{targetType}

ORDER BY create_time DESC
