mapper.update:
UPDATE t_user
SET role_code = #{roleCode}, update_time = NOW()
WHERE id = #{id}

UPDATE t_user
SET status = #{status}, update_time = NOW()
WHERE id = #{id}

UPDATE t_user
SET is_advanced = #{isAdvanced}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT COUNT(1)
FROM t_user
WHERE is_deleted = 0

    AND create_time >= #{start}


    AND create_time < #{end}

SELECT DATE(create_time) AS statDate, COUNT(1) AS total FROM t_user WHERE is_deleted = 0
AND DATE(create_time) >= #{startDate}
AND DATE(create_time) <= #{endDate}
GROUP BY DATE(create_time) ORDER BY statDate ASC
